
<?php
ob_start(); // Start output buffering

include('header.php');
include_once('controller/connect.php');

$dbs = new database();
$db = $dbs->connection();

$editemp = [];
if (isset($_GET['EmployeeId'])) {
    $empid = $_GET['EmployeeId'];
    // Fetch employee data using mysqli
    $stmt = $db->prepare("SELECT * FROM employees_data WHERE EmployeeId = ?");
    $stmt->bind_param("i", $empid);  // Bind EmployeeId as an integer
    $stmt->execute();
    $result = $stmt->get_result();  // Get the result set
    $editemp = $result->fetch_assoc();  // Fetch the employee data as an associative array
}

if ($_SERVER['REQUEST_METHOD'] == 'POST') {
    // Retrieve form data
    $empid = $_POST['EmployeeId'];
    $fname = $_POST['fname'];
    $mname = $_POST['mname'];
    $lname = $_POST['lname'];
    $gender = $_POST['gender'];
    $bdate = $_POST['bdate'];
    $marital = $_POST['marital'];
    $mnumber = $_POST['mnumber'];
    $address1 = $_POST['address1'];
    $address2 = $_POST['address2'];
    $address3 = $_POST['address3'];
    $state = $_POST['state'];
    $city = $_POST['city'];
    $aadharcard = $_POST['aadharcard'];
    $joindate = $_POST['joindate'];
    $leavedate = $_POST['leavedate'];
    $status = $_POST['status'];
    $role = $_POST['role'];
    $position = $_POST['position'];
    $email = isset($_POST['email']) ? $_POST['email'] : null;
    $password = isset($_POST['password']) ? $_POST['password'] : null;
    $salary = isset($_POST['Salary']) ? $_POST['Salary'] : null;

    // Handling file upload
    $fileName = '';
    if (!empty($_FILES['pfimg']['name'])) {
        $fileName = $_FILES['pfimg']['name'];
        $fileTmpName = $_FILES['pfimg']['tmp_name'];
        $fileDestination = 'uploads/' . $fileName;
        move_uploaded_file($fileTmpName, $fileDestination);
    } else {
        $fileName = $_POST['imagefilename']; // Keep the existing image if no new one is uploaded
    }

    // Prepare SQL statement for updating employee details
    $sql = "UPDATE employees_data SET 
                FirstName = ?, MiddleName = ?, LastName = ?, Gender = ?, Birthdate = ?, MaritalStatus = ?, Mobile = ?, 
                Address1 = ?, Address2 = ?, Address3 = ?, State = ?, City = ?, AadharNumber = ?, JoinDate = ?, 
                LeaveDate = ?, StatusId = ?, RoleId = ?, PositionId = ?, Email = ?, Password = ?, Salary = ?, 
                ProfileImage = ? WHERE EmployeeId = ?";

    $stmt = $db->prepare($sql);
    $stmt->bind_param(
        "sssssssssssssssssissssi",  // 23 types: 17 strings, 3 integers, and 1 integer for empid
        $fname, $mname, $lname, $gender, $bdate, $marital, $mnumber, $address1, $address2, $address3,
        $state, $city, $aadharcard, $joindate, $leavedate, $status, $role, $position, $email, 
        $password, $salary, $fileName, $empid
    );
  
    $stmt->execute();

    // Redirect to the detail view page after updating
    header('Location: employeeview.php?EmployeeId='.$empid);
    exit();
}

ob_end_flush(); // End output buffering
